import React, {Component} from 'react'
import Space from '../components/Space'

class Board extends Component {

  renderSquare(i) {
    return (
      <Space value={ this.props.squares[i] }
        onClick={ () => this.props.onClick(i)}
      />
    )
  }

  calculateWinner = (squares) => {
    const line = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6]
    ]
    for (let i = 0; i < line.length; i++) {
      let [a, b, c] = line[i]
      if (squares[a] && squares[a] == squares[b] && squares[a] == squares[c]) {
        return squares[a]
      }
    }
    return null
  }

  render() {
    return (
      <>
        <div className='board-row'>
          { this.renderSquare(0) }
          { this.renderSquare(1) }
          { this.renderSquare(2) }
        </div>
        <div className='board-row'>
          { this.renderSquare(3) }
          { this.renderSquare(4) }
          { this.renderSquare(5) }
        </div>
        <div className='board-row'>
          { this.renderSquare(6) }
          { this.renderSquare(7) }
          { this.renderSquare(8) }
        </div>
      </>
    )
  }
}

export default Board
